import React from "react";

export default function AddStudentForm({
  form,
  errors,
  sigOptions,
  roleOptions,
  handleChange,
  addStudent,
}) {
  return (
    <div className="mb-16">
      <div className="bg-black/40 backdrop-blur-xl border border-cyan-500/30 rounded-2xl p-8 shadow-2xl shadow-cyan-500/20">
        <h3 className="text-2xl font-bold text-cyan-300 mb-6 text-center tracking-wide">
          ADD NEW STUDENT
        </h3>

        {errors.submit && (
          <div className="mb-6 p-4 bg-red-900/20 border border-red-500/50 rounded-lg">
            <p className="text-red-400 text-center">{errors.submit}</p>
          </div>
        )}

        {/* FORM FIELDS */}
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Name */}
            <div className="relative group">
              <input
                name="name"
                placeholder="Name"
                value={form.name}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.name ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              />
              {errors.name && (
                <p className="text-red-400 text-sm mt-1">{errors.name}</p>
              )}
            </div>

            {/* Roll No */}
            <div className="relative group">
              <input
                name="rollno"
                placeholder="Roll Number"
                value={form.rollno}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.rollno ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              />
              {errors.rollno && (
                <p className="text-red-400 text-sm mt-1">{errors.rollno}</p>
              )}
            </div>

            {/* Email */}
            <div className="relative group">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={form.email}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.email ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              />
              {errors.email && (
                <p className="text-red-400 text-sm mt-1">{errors.email}</p>
              )}
            </div>

            {/* Phone */}
            <div className="relative group">
              <input
                name="phone"
                placeholder="Phone"
                value={form.phone}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.phone ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              />
              {errors.phone && (
                <p className="text-red-400 text-sm mt-1">{errors.phone}</p>
              )}
            </div>

            {/* Password */}
            <div className="relative group">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={form.password}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.password ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              />
              {errors.password && (
                <p className="text-red-400 text-sm mt-1">{errors.password}</p>
              )}
            </div>

            {/* SIG */}
            <div className="relative group">
              <select
                name="sig"
                value={form.sig}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.sig ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              >
                <option value="">Select SIG</option>
                {sigOptions.map((sig) => (
                  <option key={sig} value={sig}>
                    {sig}
                  </option>
                ))}
              </select>
              {errors.sig && (
                <p className="text-red-400 text-sm mt-1">{errors.sig}</p>
              )}
            </div>

            {/* Role */}
            <div className="relative group">
              <select
                name="role"
                value={form.role}
                onChange={handleChange}
                className={`w-full px-4 py-3 bg-slate-800/50 border ${
                  errors.role ? "border-red-500" : "border-cyan-500/30"
                } rounded-lg text-cyan-100 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20`}
              >
                <option value="">Select Role</option>
                {roleOptions.map((role) => (
                  <option key={role} value={role}>
                    {role}
                  </option>
                ))}
              </select>
              {errors.role && (
                <p className="text-red-400 text-sm mt-1">{errors.role}</p>
              )}
            </div>
          </div>

          {/* Submit button */}
          <div className="flex justify-center pt-4">
            <button
              onClick={addStudent}
              className="relative px-8 py-3 bg-gradient-to-r from-cyan-600 to-pink-600 text-white font-bold rounded-lg shadow-lg shadow-cyan-500/25 hover:shadow-pink-500/40 hover:shadow-xl transform hover:scale-105 transition-all duration-300 tracking-wide"
            >
              <span className="relative z-10">ADD STUDENT</span>
              <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-pink-600 to-cyan-600 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
