import React, { useEffect, useState } from "react";

export default function StudentsApp() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ name: "", rollno: "", email: "", sig: "", role: "" });

  // Fetch all students
  const fetchStudents = async () => {
    let res = await fetch("http://localhost:5051/");
    let data = await res.json();
    setStudents(data);
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add student
  const addStudent = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:5051/student", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ name: "", rollno: "", email: "", sig: "", role: "" });
    fetchStudents();
  };

  // Delete student
  const deleteStudent = async (id) => {
    await fetch(`http://localhost:5051/student/${id}`, { method: "DELETE" });
    fetchStudents();
  };

  return (
    <div className="min-h-screen bg-black p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-6xl font-bold bg-gradient-to-r from-cyan-400 via-blue-500 to-pink-500 bg-clip-text text-transparent mb-4 tracking-wider">
            STUDENTS
          </h1>
          <div className="h-1 w-32 mx-auto bg-gradient-to-r from-cyan-400 to-pink-500 rounded-full shadow-lg shadow-cyan-500/50"></div>
        </div>

        {/* Add Form */}
        <div className="mb-16">
          <div className="bg-black/40 backdrop-blur-xl border border-cyan-500/30 rounded-2xl p-8 shadow-2xl shadow-cyan-500/20">
            <h3 className="text-2xl font-bold text-cyan-300 mb-6 text-center tracking-wide">ADD NEW STUDENT</h3>
            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <div className="relative group">
                  <input
                    name="name"
                    placeholder="Name"
                    value={form.name}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-slate-800/50 border border-cyan-500/30 rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20 transition-all duration-300 hover:border-cyan-400/50"
                  />
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
                </div>

                <div className="relative group">
                  <input
                    name="rollno"
                    placeholder="Roll No"
                    value={form.rollno}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-slate-800/50 border border-cyan-500/30 rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20 transition-all duration-300 hover:border-cyan-400/50"
                  />
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
                </div>

                <div className="relative group">
                  <input
                    name="email"
                    placeholder="Email"
                    value={form.email}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-slate-800/50 border border-cyan-500/30 rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20 transition-all duration-300 hover:border-cyan-400/50"
                  />
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
                </div>

                <div className="relative group">
                  <input
                    name="sig"
                    placeholder="SIG"
                    value={form.sig}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-slate-800/50 border border-cyan-500/30 rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20 transition-all duration-300 hover:border-cyan-400/50"
                  />
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
                </div>

                <div className="relative group md:col-span-2 lg:col-span-1">
                  <input
                    name="role"
                    placeholder="Role"
                    value={form.role}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-slate-800/50 border border-cyan-500/30 rounded-lg text-cyan-100 placeholder-cyan-400/60 focus:outline-none focus:border-pink-500 focus:ring-2 focus:ring-pink-500/20 transition-all duration-300 hover:border-cyan-400/50"
                  />
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-cyan-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
                </div>
              </div>

              <div className="flex justify-center pt-4">
                <button
                  onClick={addStudent}
                  className="relative px-8 py-3 bg-gradient-to-r from-cyan-600 to-pink-600 text-white font-bold rounded-lg shadow-lg shadow-cyan-500/25 hover:shadow-pink-500/40 hover:shadow-xl transform hover:scale-105 transition-all duration-300 tracking-wide"
                >
                  <span className="relative z-10">ADD STUDENT</span>
                  <div className="absolute inset-0 rounded-lg bg-gradient-to-r from-pink-600 to-cyan-600 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Display Students */}
        <div>
          <div className="text-center mb-8">
            <h2 className="text-4xl font-bold bg-gradient-to-r from-pink-400 via-purple-500 to-cyan-400 bg-clip-text text-transparent tracking-wide">
              ALL STUDENTS
            </h2>
            <div className="h-0.5 w-24 mx-auto bg-gradient-to-r from-pink-400 to-cyan-400 rounded-full mt-3"></div>
          </div>

          {students.length === 0 ? (
            <div className="text-center py-16">
              <div className="bg-black/40 backdrop-blur-xl border border-cyan-500/20 rounded-2xl p-8 max-w-md mx-auto">
                <div className="w-16 h-16 mx-auto mb-4 rounded-full bg-gradient-to-r from-cyan-500/20 to-pink-500/20 flex items-center justify-center">
                  <div className="w-8 h-8 rounded-full bg-gradient-to-r from-cyan-400 to-pink-400"></div>
                </div>
                <p className="text-cyan-300 text-lg font-medium">No students in database.</p>
              </div>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {students.map((s) => (
                <div
                  key={s._id}
                  className="group bg-black/40 backdrop-blur-xl border border-pink-500/20 rounded-2xl p-6 shadow-xl shadow-pink-500/10 hover:shadow-cyan-500/20 hover:border-cyan-500/40 transition-all duration-300 hover:transform hover:scale-105"
                >
                  <div className="space-y-3">
                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium text-sm tracking-wider">ID:</span>
                      <span className="text-pink-300 font-mono text-sm bg-slate-800/50 px-2 py-1 rounded">
                        {s._id}
                      </span>
                    </div>

                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium">Name:</span>
                      <span className="text-white font-semibold">{s.name}</span>
                    </div>

                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium">Roll:</span>
                      <span className="text-pink-300 font-mono">{s.rollno}</span>
                    </div>

                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium">Email:</span>
                      <span className="text-purple-300 text-sm">{s.email}</span>
                    </div>

                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium">SIG:</span>
                      <span className="text-cyan-200">{s.sig}</span>
                    </div>

                    <div className="flex items-center space-x-2">
                      <span className="text-cyan-400 font-medium">Role:</span>
                      <span className="text-pink-200">{s.role}</span>
                    </div>
                  </div>

                  <div className="mt-6 pt-4 border-t border-pink-500/20">
                    <button
                      onClick={() => deleteStudent(s._id)}
                      className="w-full px-4 py-2 bg-gradient-to-r from-red-600 to-pink-600 text-white font-bold rounded-lg shadow-lg shadow-red-500/25 hover:shadow-pink-500/40 hover:shadow-xl transform hover:scale-105 transition-all duration-300 tracking-wide"
                    >
                      DELETE
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
