import React, { useEffect, useState } from "react";
import StudentsList from "./StudentsList.jsx";
import AddStudentForm from "./AddStudentForm.jsx"; // Note: You'll need to style this component separately

// --- HELPER COMPONENTS ---

// Icon components for cleaner JSX
const UserIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" /></svg>;
const IdIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V8a2 2 0 00-2-2h-5m-4 0V5a2 2 0 114 0v1m-4 0a2 2 0 104 0m-5 8a2 2 0 100-4 2 2 0 000 4z" /></svg>;
const MailIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" /></svg>;
const PhoneIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" /></svg>;
const LockIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" /></svg>;
const TagIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 7h.01M7 3h5a2 2 0 012 2v5a2 2 0 01-2 2H7a2 2 0 01-2-2V5a2 2 0 012-2z" /></svg>;
const RoleIcon = () => <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" /></svg>;

// Reusable component for styled form inputs
const FormInput = ({ name, value, onChange, error, placeholder, icon, ...props }) => (
  <div className="relative">
    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">{icon}</div>
    <input
      name={name}
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      className={`w-full pl-10 pr-3 py-2.5 border rounded-lg text-slate-800 bg-slate-50 focus:outline-none focus:ring-2 transition-colors duration-200 ${error ? 'border-red-400 focus:ring-red-400' : 'border-slate-200 focus:border-indigo-500 focus:ring-indigo-500'}`}
      {...props}
    />
    {error && <p className="text-red-600 text-xs mt-1 absolute">{error}</p>}
  </div>
);

// Reusable component for styled select dropdowns
const FormSelect = ({ name, value, onChange, error, children, icon }) => (
  <div className="relative">
    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">{icon}</div>
    <select
      name={name}
      value={value}
      onChange={onChange}
      className={`w-full pl-10 pr-3 py-2.5 border rounded-lg appearance-none text-slate-800 bg-slate-50 focus:outline-none focus:ring-2 transition-colors duration-200 ${error ? 'border-red-400 focus:ring-red-400' : 'border-slate-200 focus:border-indigo-500 focus:ring-indigo-500'}`}
    >
      {children}
    </select>
    {error && <p className="text-red-600 text-xs mt-1 absolute">{error}</p>}
  </div>
);


// --- MAIN APP COMPONENT ---

export default function StudentsApp() {
  // --- STATE MANAGEMENT ---
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ 
    name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" 
  });
  const [editForm, setEditForm] = useState({ 
    id: "", name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" 
  });
  const [errors, setErrors] = useState({});
  const [editErrors, setEditErrors] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  // --- DATA & LOGIC FUNCTIONS ---

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const res = await fetch("http://localhost:5051/");
      const data = await res.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
      // Fallback with demo data if the API fails
      setStudents([
        { _id: "demo1", name: "John Doe", rollno: "AM.SC.U4CSE23001", email: "am.sc.u4cse23001@am.students.amrita.edu", phone: "9876543210", sig: "WEB", role: "CORE", createdAt: new Date().toISOString() }
      ]);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Form input change handlers
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm({ ...editForm, [name]: value });
    if (editErrors[name]) {
      setEditErrors({ ...editErrors, [name]: "" });
    }
  };

  // Validation logic
  const validateRollNo = (rollno) => /^AM\.(SC|SE|SB|SA|EN|CB)\.(U[1-9])[A-Z]{2,4}\d{5}$/.test(rollno);
  const validateEmail = (email) => /^am\.(sc|se|sb|sa|en|cb)\.u\d{1}[a-z]{2,4}\d{5}@am\.students\.amrita\.edu$/.test(email);
  const validatePhone = (phone) => /^[6-9]\d{9}$/.test(phone);

  const validateForm = () => {
    const newErrors = {};
    if (!form.name.trim()) newErrors.name = "Name is required";
    if (!form.rollno.trim()) newErrors.rollno = "Roll number is required";
    else if (!validateRollNo(form.rollno)) newErrors.rollno = "Invalid roll number format";
    if (!form.email.trim()) newErrors.email = "Email is required";
    else if (!validateEmail(form.email)) newErrors.email = "Invalid email format";
    if (!form.phone.trim()) newErrors.phone = "Phone is required";
    else if (!validatePhone(form.phone)) newErrors.phone = "Invalid 10-digit phone number";
    if (!form.password.trim()) newErrors.password = "Password is required";
    else if (form.password.length < 6) newErrors.password = "Password must be at least 6 characters";
    if (!form.sig.trim()) newErrors.sig = "SIG is required";
    if (!form.role) newErrors.role = "Role is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateEditForm = () => {
    const newErrors = {};
    if (!editForm.name.trim()) newErrors.name = "Name is required";
    if (!editForm.rollno.trim()) newErrors.rollno = "Roll number is required";
    else if (!validateRollNo(editForm.rollno)) newErrors.rollno = "Invalid roll number format";
    if (!editForm.email.trim()) newErrors.email = "Email is required";
    else if (!validateEmail(editForm.email)) newErrors.email = "Invalid email format";
    if (!editForm.phone.trim()) newErrors.phone = "Phone is required";
    else if (!validatePhone(editForm.phone)) newErrors.phone = "Invalid 10-digit phone number";
    if (editForm.password && editForm.password.length < 6) newErrors.password = "Password must be at least 6 characters";
    if (!editForm.sig.trim()) newErrors.sig = "SIG is required";
    if (!editForm.role) newErrors.role = "Role is required";
    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // CRUD operations
  const addStudent = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      const response = await fetch("http://localhost:5051/student", {
        method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(form),
      });
      if (response.ok) {
        setForm({ name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
        setErrors({});
        fetchStudents();
      } else {
        setErrors({ submit: await response.text() });
      }
    } catch (error) {
      setErrors({ submit: "A network error occurred." });
    }
  };

  const startEdit = (student) => {
    setEditForm({
      id: student._id, name: student.name, rollno: student.rollno, email: student.email,
      sig: student.sig, role: student.role, password: "", phone: student.phone
    });
    setEditErrors({});
    setIsEditing(true);
  };

  const cancelEdit = () => {
    setIsEditing(false);
  };

  const updateStudent = async (e) => {
    e.preventDefault();
    if (!validateEditForm()) return;
    try {
      const { id, password, ...updateData } = editForm;
      if (password.trim()) {
        updateData.password = password;
      }
      const response = await fetch(`http://localhost:5051/student/${id}`, {
        method: "PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify(updateData),
      });
      if (response.ok) {
        setIsEditing(false);
        fetchStudents();
      } else {
        setEditErrors({ submit: await response.text() });
      }
    } catch (error) {
      setEditErrors({ submit: "A network error occurred." });
    }
  };

  const deleteStudent = async (id) => {
    try {
      await fetch(`http://localhost:5051/student/${id}`, { method: "DELETE" });
      fetchStudents();
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  const sigOptions = ["WEB", "APP", "AI", "GLITCH", "CYBER"];
  const roleOptions = ["CORE", "CO-LEAD", "LEAD", "CHAIRPERSON", "VICE-CHAIRPERSON", "WEB-MASTER", "SECRETARY"];

  // --- JSX RENDER ---
  return (
    <div className="min-h-screen bg-slate-100 text-slate-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <header className="text-center mb-12">
          <h1 className="text-4xl sm:text-5xl font-bold text-slate-900 tracking-tight">
            Student Management
          </h1>
          <div className="h-1.5 w-24 mx-auto bg-indigo-600 rounded-full mt-4"></div>
        </header>

        <div className="bg-white p-6 sm:p-8 rounded-xl shadow-sm mb-12">
          <h2 className="text-2xl font-bold text-slate-800 mb-6">Add New Student</h2>
          <AddStudentForm
            form={form} errors={errors} sigOptions={sigOptions} roleOptions={roleOptions}
            handleChange={handleChange} addStudent={addStudent}
          />
        </div>

        <StudentsList students={students} startEdit={startEdit} deleteStudent={deleteStudent} />
      </div>

      {/* Edit Modal */}
      {isEditing && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center p-4 z-50 transition-opacity duration-300 animate-fade-in">
          <div className="bg-white rounded-xl shadow-2xl max-w-4xl w-full max-h-[90vh] flex flex-col animate-slide-up">
            <header className="flex items-center justify-between p-6 border-b border-slate-200">
              <h3 className="text-xl font-bold text-slate-900">Edit Student Details</h3>
              <button onClick={cancelEdit} className="p-1 rounded-full text-slate-400 hover:bg-slate-100 hover:text-slate-600">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>
              </button>
            </header>

            <form onSubmit={updateStudent} className="p-6 sm:p-8 overflow-y-auto space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-6 gap-y-8">
                <FormInput name="name" value={editForm.name} onChange={handleEditChange} error={editErrors.name} placeholder="Full Name" icon={<UserIcon />} />
                <FormInput name="rollno" value={editForm.rollno} onChange={handleEditChange} error={editErrors.rollno} placeholder="Roll Number" icon={<IdIcon />} />
                <FormInput name="email" value={editForm.email} onChange={handleEditChange} error={editErrors.email} placeholder="Email Address" icon={<MailIcon />} />
                <FormInput name="phone" value={editForm.phone} onChange={handleEditChange} error={editErrors.phone} placeholder="Phone Number" maxLength="10" icon={<PhoneIcon />} />
                <FormInput name="password" type="password" value={editForm.password} onChange={handleEditChange} error={editErrors.password} placeholder="New Password (Optional)" icon={<LockIcon />} />
                <FormSelect name="sig" value={editForm.sig} onChange={handleEditChange} error={editErrors.sig} icon={<TagIcon />}>
                  <option value="">Select SIG</option>
                  {sigOptions.map(sig => <option key={sig} value={sig}>{sig}</option>)}
                </FormSelect>
                <div className="lg:col-span-3">
                  <FormSelect name="role" value={editForm.role} onChange={handleEditChange} error={editErrors.role} icon={<RoleIcon />}>
                    <option value="">Select Role</option>
                    {roleOptions.map(role => <option key={role} value={role}>{role}</option>)}
                  </FormSelect>
                </div>
              </div>

              <footer className="flex justify-end gap-4 pt-4">
                <button type="button" onClick={cancelEdit} className="px-6 py-2.5 bg-slate-200 text-slate-700 font-bold rounded-lg hover:bg-slate-300 transition-colors duration-200">
                  Cancel
                </button>
                <button type="submit" className="px-6 py-2.5 bg-indigo-600 text-white font-bold rounded-lg hover:bg-indigo-700 transition-colors duration-200 flex items-center gap-2">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" /></svg>
                  Update Student
                </button>
              </footer>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
