import React, { useEffect, useState } from "react";

export default function StudentsApp() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ name: "", rollno: "", email: "", sig: "", role: "" });

  // Fetch all students
  const fetchStudents = async () => {
    let res = await fetch("http://localhost:5051/");
    let data = await res.json();
    setStudents(data);
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add student
  const addStudent = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:5051/student", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ name: "", rollno: "", email: "", sig: "", role: "" });
    fetchStudents();
  };

  // Delete student
  const deleteStudent = async (id) => {
    await fetch(`http://localhost:5051/student/${id}`, { method: "DELETE" });
    fetchStudents();
  };

  return (
    <div>
      <h1>Students</h1>

      {/* Add Form */}
      <form onSubmit={addStudent}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} />
        <input name="rollno" placeholder="Roll No" value={form.rollno} onChange={handleChange} />
        <input name="email" placeholder="Email" value={form.email} onChange={handleChange} />
        <input name="sig" placeholder="SIG" value={form.sig} onChange={handleChange} />
        <input name="role" placeholder="Role" value={form.role} onChange={handleChange} />
        <button type="submit">Add Student</button>
      </form>

      {/* Display Students */}
      <h2>All Students</h2>
      {students.length === 0 ? (
        <p>No students in database.</p>
      ) : (
        students.map((s) => (
          <div key={s._id}>
            <p><strong>ID:</strong> {s._id}</p>
            <p><strong>Name:</strong> {s.name}</p>
            <p><strong>Roll No:</strong> {s.rollno}</p>
            <p><strong>Email:</strong> {s.email}</p>
            <p><strong>SIG:</strong> {s.sig}</p>
            <p><strong>Role:</strong> {s.role}</p>
            <button onClick={() => deleteStudent(s._id)}>Delete</button>
            <hr />
          </div>
        ))
      )}
    </div>
  );
}
