import React, { useState, useEffect } from 'react';
import { Calendar, User, Clock, Edit, Trash2, Plus, Search } from 'lucide-react';

const AttendanceManagement = () => {
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingRecord, setEditingRecord] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStudent, setSelectedStudent] = useState('');
  const [formData, setFormData] = useState({
    student_id: '',
    date: '',
    status: 'present',
    time_in: '',
    time_out: '',
    notes: ''
  });

  // Mock data for demonstration (replace with actual API calls)
  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      const mockStudents = [
        { _id: '1', name: 'John Doe', roll_number: 'CS001' },
        { _id: '2', name: 'Jane Smith', roll_number: 'CS002' },
        { _id: '3', name: 'Mike Johnson', roll_number: 'CS003' }
      ];

      const mockAttendance = [
        {
          _id: '1',
          student_id: { _id: '1', name: 'John Doe', roll_number: 'CS001' },
          date: '2025-08-29',
          status: 'present',
          time_in: '09:00',
          time_out: '17:00',
          notes: 'On time'
        },
        {
          _id: '2',
          student_id: { _id: '2', name: 'Jane Smith', roll_number: 'CS002' },
          date: '2025-08-29',
          status: 'absent',
          time_in: '',
          time_out: '',
          notes: 'Medical leave'
        }
      ];

      setStudents(mockStudents);
      setAttendanceRecords(mockAttendance);
      setLoading(false);
    }, 1000);
  }, []);

  const handleSubmit = (e) => {
    // Here you would make API call to create/update attendance
    if (editingRecord) {
      // Update existing record
      const updated = attendanceRecords.map(record => 
        record._id === editingRecord._id 
          ? { ...record, ...formData, student_id: students.find(s => s._id === formData.student_id) }
          : record
      );
      setAttendanceRecords(updated);
    } else {
      // Create new record
      const newRecord = {
        ...formData,
        _id: Date.now().toString(),
        student_id: students.find(s => s._id === formData.student_id)
      };
      setAttendanceRecords([...attendanceRecords, newRecord]);
    }

    resetForm();
  };

  const resetForm = () => {
    setFormData({
      student_id: '',
      date: '',
      status: 'present',
      time_in: '',
      time_out: '',
      notes: ''
    });
    setShowForm(false);
    setEditingRecord(null);
  };

  const handleEdit = (record) => {
    setEditingRecord(record);
    setFormData({
      student_id: record.student_id._id,
      date: record.date,
      status: record.status,
      time_in: record.time_in,
      time_out: record.time_out,
      notes: record.notes
    });
    setShowForm(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this record?')) {
      setAttendanceRecords(attendanceRecords.filter(record => record._id !== id));
    }
  };

  const filteredRecords = attendanceRecords.filter(record => {
    const matchesSearch = record.student_id.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         record.student_id.roll_number.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStudent = selectedStudent === '' || record.student_id._id === selectedStudent;
    return matchesSearch && matchesStudent;
  });

  if (loading) {
    return (
      <div className="min-h-screen bg-black text-white flex items-center justify-center" style={{fontFamily: 'Orbitron, sans-serif'}}>
        <div className="text-yellow-400 text-2xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white p-5" style={{fontFamily: 'Orbitron, sans-serif'}}>
      {/* Header */}
      <div className="text-center mb-10">
        <h1 className="text-5xl font-bold text-yellow-400 mb-3 tracking-wide">ATTENDANCE SYSTEM</h1>
        <div className="h-1 w-24 bg-yellow-400 mx-auto"></div>
      </div>

      {/* Controls */}
      <div className="max-w-6xl mx-auto mb-8">
        <div className="flex flex-col md:flex-row gap-4 mb-6">
          {/* Search */}
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-yellow-400" size={20} />
            <input
              type="text"
              placeholder="Search by name or roll number..."
              className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-12 text-white placeholder-gray-400 focus:outline-none focus:border-white transition-colors"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          {/* Student Filter */}
          <select
            className="bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
            value={selectedStudent}
            onChange={(e) => setSelectedStudent(e.target.value)}
          >
            <option value="">All Students</option>
            {students.map(student => (
              <option key={student._id} value={student._id}>
                {student.name} ({student.roll_number})
              </option>
            ))}
          </select>

          {/* Add Button */}
          <button
            onClick={() => setShowForm(true)}
            className="flex items-center gap-2 bg-yellow-400 text-black px-6 py-3 rounded-lg font-bold hover:bg-white transition-colors"
          >
            <Plus size={20} />
            ADD RECORD
          </button>
        </div>
      </div>

      {/* Form Modal */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-black border-2 border-yellow-400 rounded-lg p-8 max-w-md w-full mx-4">
            <h2 className="text-2xl font-bold text-yellow-400 mb-6">
              {editingRecord ? 'EDIT RECORD' : 'NEW RECORD'}
            </h2>

            <div className="space-y-4">
              <div>
                <label className="block text-white mb-2 font-medium">Student</label>
                <select
                  required
                  className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
                  value={formData.student_id}
                  onChange={(e) => setFormData({...formData, student_id: e.target.value})}
                >
                  <option value="">Select Student</option>
                  {students.map(student => (
                    <option key={student._id} value={student._id}>
                      {student.name} ({student.roll_number})
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-white mb-2 font-medium">Date</label>
                <input
                  type="date"
                  required
                  className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
                  value={formData.date}
                  onChange={(e) => setFormData({...formData, date: e.target.value})}
                />
              </div>

              <div>
                <label className="block text-white mb-2 font-medium">Status</label>
                <select
                  required
                  className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
                  value={formData.status}
                  onChange={(e) => setFormData({...formData, status: e.target.value})}
                >
                  <option value="present">Present</option>
                  <option value="absent">Absent</option>
                  <option value="late">Late</option>
                </select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-white mb-2 font-medium">Time In</label>
                  <input
                    type="time"
                    className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
                    value={formData.time_in}
                    onChange={(e) => setFormData({...formData, time_in: e.target.value})}
                  />
                </div>
                <div>
                  <label className="block text-white mb-2 font-medium">Time Out</label>
                  <input
                    type="time"
                    className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors"
                    value={formData.time_out}
                    onChange={(e) => setFormData({...formData, time_out: e.target.value})}
                  />
                </div>
              </div>

              <div>
                <label className="block text-white mb-2 font-medium">Notes</label>
                <textarea
                  className="w-full bg-black border-2 border-yellow-400 rounded-lg py-3 px-4 text-white focus:outline-none focus:border-white transition-colors h-20 resize-none"
                  value={formData.notes}
                  onChange={(e) => setFormData({...formData, notes: e.target.value})}
                  placeholder="Optional notes..."
                />
              </div>

              <div className="flex gap-4 pt-4">
                <button
                  type="button"
                  onClick={handleSubmit}
                  className="flex-1 bg-yellow-400 text-black py-3 rounded-lg font-bold hover:bg-white transition-colors"
                >
                  {editingRecord ? 'UPDATE' : 'CREATE'}
                </button>
                <button
                  type="button"
                  onClick={resetForm}
                  className="flex-1 bg-black border-2 border-yellow-400 text-white py-3 rounded-lg font-bold hover:bg-yellow-400 hover:text-black transition-colors"
                >
                  CANCEL
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Records Grid */}
      <div className="max-w-6xl mx-auto">
        {filteredRecords.length === 0 ? (
          <div className="text-center py-16">
            <div className="bg-black border-2 border-yellow-400 rounded-lg p-8 max-w-md mx-auto">
              <Calendar className="w-16 h-16 text-yellow-400 mx-auto mb-4" />
              <p className="text-yellow-400 text-lg font-medium">No attendance records found</p>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredRecords.map(record => (
              <div
                key={record._id}
                className="bg-black border-2 border-yellow-400 rounded-lg p-6 hover:border-white transition-colors"
              >
                <div className="flex-1">
                  <h3 className="text-xl font-bold text-yellow-400 mb-2 tracking-wide">
                    {record.student_id.name}
                  </h3>
                  <p className="text-white font-mono mb-4">{record.student_id.roll_number}</p>

                  <div className="space-y-2 mb-4">
                    <div className="flex text-sm">
                      <span className="text-white font-medium w-20 flex-shrink-0">Date:</span>
                      <span className="text-yellow-400">{record.date}</span>
                    </div>
                    <div className="flex text-sm">
                      <span className="text-white font-medium w-20 flex-shrink-0">Status:</span>
                      <span className={`font-bold ${
                        record.status === 'present' ? 'text-green-400' : 
                        record.status === 'absent' ? 'text-red-400' : 'text-orange-400'
                      }`}>
                        {record.status.toUpperCase()}
                      </span>
                    </div>
                    {record.time_in && (
                      <div className="flex text-sm">
                        <span className="text-white font-medium w-20 flex-shrink-0">Time In:</span>
                        <span className="text-yellow-400">{record.time_in}</span>
                      </div>
                    )}
                    {record.time_out && (
                      <div className="flex text-sm">
                        <span className="text-white font-medium w-20 flex-shrink-0">Time Out:</span>
                        <span className="text-yellow-400">{record.time_out}</span>
                      </div>
                    )}
                    {record.notes && (
                      <div className="flex text-sm">
                        <span className="text-white font-medium w-20 flex-shrink-0">Notes:</span>
                        <span className="text-yellow-400 break-words">{record.notes}</span>
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex gap-3 pt-4">
                  <button
                    onClick={() => handleEdit(record)}
                    className="flex-1 bg-black border-2 border-yellow-400 text-white py-2 px-4 rounded-lg font-bold hover:bg-yellow-400 hover:text-black transition-colors flex items-center justify-center gap-2"
                  >
                    <Edit size={16} />
                    EDIT
                  </button>
                  <button
                    onClick={() => handleDelete(record._id)}
                    className="flex-1 bg-yellow-400 text-black py-2 px-4 rounded-lg font-bold hover:bg-white transition-colors flex items-center justify-center gap-2"
                  >
                    <Trash2 size={16} />
                    DELETE
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AttendanceManagement;
