import React, { useEffect, useState } from "react";
import StudentsList from "./StudentsList.jsx";
import AddStudentForm from "./AddStudentForm.jsx";

export default function StudentsApp() {
  // State management and logic remain the same. No changes here.
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ 
    name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" 
  });
  const [editForm, setEditForm] = useState({ 
    id: "", name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" 
  });
  const [errors, setErrors] = useState({});
  const [editErrors, setEditErrors] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  // All functions (fetch, handle change, validation, add, edit, delete) are unchanged.
  const fetchStudents = async () => {
    try {
      let res = await fetch("http://localhost:5051/");
      let data = await res.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
      setStudents([
        {
          _id: "demo1",
          name: "John Doe",
          rollno: "AM.SC.U4CSE23001",
          email: "am.sc.u4cse23001@am.students.amrita.edu",
          phone: "9876543210",
          sig: "WEB",
          role: "CORE",
          createdAt: new Date().toISOString()
        }
      ]);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: "" });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm({ ...editForm, [name]: value });
    if (editErrors[name]) setEditErrors({ ...editErrors, [name]: "" });
  };

  const validateRollNo = (rollno) => /^AM\.(SC|SE|SB|SA|EN|CB)\.(U[1-9])[A-Z]{2,4}\d{5}$/.test(rollno);
  const validateEmail = (email) => /^am\.(sc|se|sb|sa|en|cb)\.u\d{1}[a-z]{2,4}\d{5}@am\.students\.amrita\.edu$/.test(email);
  const validatePhone = (phone) => /^[6-9]\d{9}$/.test(phone);

  const validateForm = () => { /* ... validation logic is unchanged ... */ 
    const newErrors = {};
    if (!form.name.trim()) newErrors.name = "Name is required";
    if (!form.rollno.trim()) newErrors.rollno = "Roll number is required";
    else if (!validateRollNo(form.rollno)) newErrors.rollno = "Roll number format: AM.SC.U4CSE23029";
    if (!form.email.trim()) newErrors.email = "Email is required";
    else if (!validateEmail(form.email)) newErrors.email = "Email format: am.sc.u4cse23029@am.students.amrita.edu";
    if (!form.phone.trim()) newErrors.phone = "Phone number is required";
    else if (!validatePhone(form.phone)) newErrors.phone = "Phone number must be a valid 10-digit Indian number";
    if (!form.password.trim()) newErrors.password = "Password is required";
    else if (form.password.length < 6) newErrors.password = "Password must be at least 6 characters";
    if (!form.sig.trim()) newErrors.sig = "SIG is required";
    if (!form.role) newErrors.role = "Role is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateEditForm = () => { /* ... validation logic is unchanged ... */
    const newErrors = {};
    if (!editForm.name.trim()) newErrors.name = "Name is required";
    if (!editForm.rollno.trim()) newErrors.rollno = "Roll number is required";
    else if (!validateRollNo(editForm.rollno)) newErrors.rollno = "Roll number format: AM.SC.U4CSE23029";
    if (!editForm.email.trim()) newErrors.email = "Email is required";
    else if (!validateEmail(editForm.email)) newErrors.email = "Email format: am.sc.u4cse23029@am.students.amrita.edu";
    if (!editForm.phone.trim()) newErrors.phone = "Phone number is required";
    else if (!validatePhone(editForm.phone)) newErrors.phone = "Phone number must be a valid 10-digit Indian number";
    if (editForm.password && editForm.password.length < 6) newErrors.password = "Password must be at least 6 characters";
    if (!editForm.sig.trim()) newErrors.sig = "SIG is required";
    if (!editForm.role) newErrors.role = "Role is required";
    setEditErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const addStudent = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    try {
      const response = await fetch("http://localhost:5051/student", {
        method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify(form),
      });
      if (response.ok) {
        setForm({ name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
        setErrors({});
        fetchStudents();
      } else {
        setErrors({ submit: await response.text() });
      }
    } catch (error) {
      setErrors({ submit: "Network error occurred" });
    }
  };

  const startEdit = (student) => {
    setEditForm({
      id: student._id, name: student.name, rollno: student.rollno, email: student.email,
      sig: student.sig, role: student.role, password: "", phone: student.phone
    });
    setEditErrors({});
    setIsEditing(true);
  };

  const cancelEdit = () => {
    setEditForm({ id: "", name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
    setEditErrors({});
    setIsEditing(false);
  };

  const updateStudent = async (e) => {
    e.preventDefault();
    if (!validateEditForm()) return;
    try {
      const { id, password, ...updateData } = editForm;
      if (password.trim()) updateData.password = password;
      const response = await fetch(`http://localhost:5051/student/${id}`, {
        method: "PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify(updateData),
      });
      if (response.ok) {
        setIsEditing(false);
        setEditForm({ id: "", name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
        setEditErrors({});
        fetchStudents();
      } else {
        setEditErrors({ submit: await response.text() });
      }
    } catch (error) {
      setEditErrors({ submit: "Network error occurred" });
    }
  };

  const deleteStudent = async (id) => {
    try {
      await fetch(`http://localhost:5051/student/${id}`, { method: "DELETE" });
      fetchStudents();
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  const sigOptions = ["WEB", "APP", "AI", "GLITCH", "CYBER"];
  const roleOptions = ["CORE", "CO-LEAD", "LEAD", "CHAIRPERSON", "VICE-CHAIRPERSON", "WEB-MASTER", "SECRETARY"];

  return (
    // Changed background to a light gray for a more standard app feel.
    <div className="min-h-screen bg-gray-50 p-4 sm:p-6 lg:p-8">
      <div className="max-w-7xl mx-auto">
        {/* Header updated to solid text and accent color */}
        <div className="text-center mb-12">
          <h1 className="text-5xl sm:text-6xl font-bold text-gray-800 mb-4 tracking-tight">
            Students
          </h1>
          <div className="h-1 w-32 mx-auto bg-blue-600 rounded-full"></div>
        </div>

        {/* The AddStudentForm component is rendered here. 
            You'll want to update its internal styles to match this new theme. */}
        <AddStudentForm
          form={form}
          errors={errors}
          sigOptions={sigOptions}
          roleOptions={roleOptions}
          handleChange={handleChange}
          addStudent={addStudent}
        />

        <hr className="my-12 border-gray-200" />

        {/* Edit Modal: The entire modal has been restyled */}
        {isEditing && (
          // Switched to a standard semi-transparent gray overlay
          <div className="fixed inset-0 bg-gray-900/50 flex items-center justify-center p-4 z-50">
            {/* Modal panel is now a solid white card with a standard shadow */}
            <div className="bg-white rounded-lg p-6 sm:p-8 shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
              <h3 className="text-2xl font-bold text-gray-800 mb-6 text-center">Edit Student</h3>

              {/* Standard error message styling */}
              {editErrors.submit && (
                <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                  <p className="text-red-700 text-center">{editErrors.submit}</p>
                </div>
              )}

              {/* Form fields updated for a standard look */}
              <form onSubmit={updateStudent}>
                <div className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {/* Simplified input fields without the extra wrappers and effects */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                      <input
                        name="name"
                        value={editForm.name}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.name ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      />
                      {editErrors.name && <p className="text-red-600 text-sm mt-1">{editErrors.name}</p>}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Roll No</label>
                      <input
                        name="rollno"
                        value={editForm.rollno}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.rollno ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      />
                      {editErrors.rollno && <p className="text-red-600 text-sm mt-1">{editErrors.rollno}</p>}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                      <input
                        name="email"
                        value={editForm.email}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.email ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      />
                      {editErrors.email && <p className="text-red-600 text-sm mt-1">{editErrors.email}</p>}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                      <input
                        name="phone"
                        value={editForm.phone}
                        onChange={handleEditChange}
                        maxLength="10"
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.phone ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      />
                      {editErrors.phone && <p className="text-red-600 text-sm mt-1">{editErrors.phone}</p>}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">New Password</label>
                      <input
                        name="password"
                        type="password"
                        placeholder="Leave empty to keep current"
                        value={editForm.password}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.password ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      />
                      {editErrors.password && <p className="text-red-600 text-sm mt-1">{editErrors.password}</p>}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">SIG</label>
                      <select
                        name="sig"
                        value={editForm.sig}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.sig ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      >
                        <option value="">Select SIG</option>
                        {sigOptions.map(sig => <option key={sig} value={sig}>{sig}</option>)}
                      </select>
                      {editErrors.sig && <p className="text-red-600 text-sm mt-1">{editErrors.sig}</p>}
                    </div>

                    <div className="lg:col-span-3">
                      <label className="block text-sm font-medium text-gray-700 mb-1">Role</label>
                      <select
                        name="role"
                        value={editForm.role}
                        onChange={handleEditChange}
                        className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 ${editErrors.role ? 'border-red-400 focus:ring-red-400' : 'border-gray-300 focus:ring-blue-500'}`}
                      >
                        <option value="">Select Role</option>
                        {roleOptions.map(role => <option key={role} value={role}>{role}</option>)}
                      </select>
                      {editErrors.role && <p className="text-red-600 text-sm mt-1">{editErrors.role}</p>}
                    </div>
                  </div>

                  {/* Buttons updated to solid colors */}
                  <div className="flex justify-end gap-4 pt-4">
                    <button
                      type="button"
                      onClick={cancelEdit}
                      className="px-6 py-2 bg-gray-200 text-gray-800 font-bold rounded-lg hover:bg-gray-300 transition-colors duration-300"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      className="px-6 py-2 bg-green-600 text-white font-bold rounded-lg hover:bg-green-700 transition-colors duration-300"
                    >
                      Update Student
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        )}

        <StudentsList 
          students={students} 
          startEdit={startEdit} 
          deleteStudent={deleteStudent} 
        />

      </div>
    </div>
  );
}
