import React, { useEffect, useState } from "react";
import StudentsDisplay from "./StudentsDisplay";

export default function StudentsApp() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
  const [errors, setErrors] = useState({});

  // Fetch all students
  const fetchStudents = async () => {
    let res = await fetch("http://localhost:5051/");
    let data = await res.json();
    setStudents(data);
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };

  // Validation functions
  const validateRollNo = (rollno) => {
    const rollPattern = /^AM\.(SC|SE|SB|SA|EN|CB)\.(U[1-9])[A-Z]{2,4}\d{5}$/;
    return rollPattern.test(rollno);
  };

  const validateEmail = (email) => {
    const emailPattern = /^am\.(sc|se|sb|sa|en|cb)\.(u[1-9])[a-z]{2,4}\d{5}@am\.amrita\.edu$/;
    return emailPattern.test(email);
  };

  const validatePhone = (phone) => {
    const phonePattern = /^(\+91-)?[6-9]\d{9}$/;
    return phonePattern.test(phone);
  };

  const validateForm = () => {
    const newErrors = {};

    if (!form.name.trim()) {
      newErrors.name = "Name is required";
    }

    if (!form.rollno.trim()) {
      newErrors.rollno = "Roll number is required";
    } else if (!validateRollNo(form.rollno)) {
      newErrors.rollno = "Roll number format: AM.SC.U4CSE23029";
    }

    if (!form.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!validateEmail(form.email)) {
      newErrors.email = "Email format: am.sc.u4cse23029@am.amrita.edu";
    }

    if (!form.sig.trim()) {
      newErrors.sig = "SIG is required";
    }

    if (!form.role) {
      newErrors.role = "Role is required";
    }

    if (!form.password.trim()) {
      newErrors.password = "Password is required";
    } else if (form.password.length < 8) {
      newErrors.password = "Password must be at least 8 characters";
    }

    if (!form.phone.trim()) {
      newErrors.phone = "Phone number is required";
    } else if (!validatePhone(form.phone)) {
      newErrors.phone = "Phone format: +91-9876543210 or 9876543210";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Add student
  const addStudent = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }
    await fetch("http://localhost:5051/student", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ name: "", rollno: "", email: "", sig: "", role: "", password: "", phone: "" });
    setErrors({});
    fetchStudents();
  };

  // Delete student
  const deleteStudent = async (id) => {
    await fetch(`http://localhost:5051/student/${id}`, { method: "DELETE" });
    fetchStudents();
  };

