                  >
                    <option value="" className="bg-slate-800 text-cyan-400">Select Role</option>
                    <option value="CORE" className="bg-slate-800 text-cyan-100">CORE</option>
                    <option value="CO-LEAD" className="bg-slate-800 text-cyan-100">CO-LEAD</option>
                    <option value="LEAD" className="bg-slate-800 text-cyan-100">LEAD</option>
                    <option value="CHAIRPERSON" className="bg-slate-800 text-cyan-100">CHAIRPERSON</option>
